azureLogAnalytics.query:
let r = requests | where $__timeFilter(timestamp);
let f = coalesce(toscalar(r | summarize min(timestamp)), ago(1d));
let t = coalesce(toscalar(r | summarize max(timestamp)), now());
let span=(t - f)/60;
let interval=case(span >= 1d, bin(span, 1d), span >= 1h, bin(span, 1h), 15m);
let intervalHours = interval / 1h;
r
| where success == false
| make-series kind=nonempty valueCount=count() default=0 on timestamp in range(f, t, interval)
| mv-expand timestamp to typeof(datetime), valueCount to typeof(double)
| project timestamp, failuresCount=valueCount/intervalHours
